import axios from "axios";

const API_URL = 'http://localhost:8000/api/';

class ItemService {

    getItems(){
        return axios.get(API_URL + "getitems");
    }

    addItem(){
        return axios.post(API_URL + "additem");
    }
}

export default new ItemService();
